import React from 'react';
import styled from 'styled-components';

const History = ({ userState, setUserState }) => {
    localStorage.setItem([userState.data.login], JSON.stringify(userState));

    const onHistoryClick = (e) => {
        // console.log(e.target.innerText);
        setUserState(JSON.parse(localStorage[e.target.innerText]));
    };
    const rendering = () => {
        const result = [];
        for (let i = 0; i < localStorage.length; i++) {
            console.log(localStorage.getItem(i));
            result.push(
                <div key={localStorage.key(i).data} className="history">
                    <div className="id" onClick={onHistoryClick}>
                        {localStorage.key(i)}
                    </div>
                    <i className="fas fa-trash"></i>
                </div>
            );
        }
        console.log(result);
        return result;
    };
    console.log(localStorage);
    return (
        <Container>
            <div className="history__text">History</div>
            {rendering()}
        </Container>
    );
};

const Container = styled.section`
    position: fixed;
    top: 20%;
    left: 10px;

    .history__text {
        font-size: 24px;
        color: white;
    }
    .history {
        display: flex;
        justify-content: space-between;
        margin-top: 8px;
        .id {
            cursor: pointer;
            /* margin-top: 8px; */
            margin-right: 5px;
            &:hover {
                color: blue;
            }
        }
        i {
            cursor: pointer;
            color: red;
        }
    }
`;

export default History;
